CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_A_AddCustomerPayment` (IN `_InvoiceID` INT, IN `_CreatedBy` INT, IN `_Status` INT, IN `_UserType` VARCHAR(50), IN `_IPAddress` VARCHAR(15), IN `_PaymentAmount` DOUBLE, IN `_GSTAmount` DOUBLE, IN `_AmountType` INT, IN `_PaymentDate` DATE, IN `_PaymentMode` ENUM('Cheque','Online','Cash','Other','Bank Transfer'), IN `_ChequeNo` VARCHAR(9), IN `_IFCCode` VARCHAR(16), IN `_AccountNo` VARCHAR(25), IN `_HolderName` VARCHAR(25), IN `_BankName` VARCHAR(250), IN `_BranchName` VARCHAR(100), IN `_Remark` TEXT, IN `_AppointmentID` INT)  NO SQL
BEGIN
DECLARE EXIT handler for sqlexception
  BEGIN
    GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @textMsg = MESSAGE_TEXT; 
