    ROLLBACK;
    SET @errorlog = (SELECT Fn_AddErrorlog("Add CustomerPayment",IFNULL(@textMsg,''),_UserType,'Database',_IPAddress,_CreatedBy));
  END;
  START TRANSACTION;
SET @TimeZone = (SELECT Fn_TimeZone());
INSERT INTO ss_customerpayment(InvoiceID,PaymentAmount,GSTAmount,AmountType,PaymentDate,PaymentMode,ChequeNo,IFCCode,AccountNo,HolderName,BankName,BranchName,Remark,CreatedDate,CreatedBy,Status)
VALUES (
      IFNULL(_InvoiceID,0),
      IFNULL(_PaymentAmount,0),
      IFNULL(_GSTAmount,0),
      IFNULL(_AmountType,0),
      IFNULL(_PaymentDate,''),
      IFNULL(_PaymentMode,''),
      IFNULL(_ChequeNo,''),
      IFNULL(_IFCCode,''),
      IFNULL(_AccountNo,''),
      IFNULL(_HolderName,''),
      IFNULL(_BankName,''),
      IFNULL(_BranchName,''),
      IFNULL(_Remark,''),
      CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),    
      IFNULL(_CreatedBy,0),
      IFNULL(_Status,0)
    );
      
    SET @LAST_ID = LAST_INSERT_ID();
    
    SET @VisitorName = (select V.Name from ss_invoice I LEFT JOIN ss_customer C ON C.CustomerID=I.CustomerID LEFT JOIN ss_visitor V ON V.VisitorID=C.VisitorID WHERE I.InvoiceID=_InvoiceID);
    SET @UserName  = Fn_GetUserNameByUserID(_CreatedBy);
    SET @DESC = CONCAT(@UserName,' has added a customer payment for ',@VisitorName);
    SET @ProcessID=(SELECT Fn_AddProcess(@LAST_ID,'Customer',@DESC,@LAST_ID,'Add Customer Payment',_CreatedBy,@LAST_ID));

    IF _AppointmentID = 0 THEN
      SET @RemainingPayment = (SELECT 
          ROUND(IFNULL(D.SubTotal-IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'Payment','Invoice'),0),0),2) AS RemainingPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
      SET @RemainingGSTPayment = (SELECT 
          ROUND(IFNULL((D.IGST+0+D.CGST+D.SGST-IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'','Invoice'),0)),0),2) AS RemainingGSTPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
          
        SET @TotalRemainingAmount = (SELECT ROUND(IFNULL(
            D.SubTotal + D.IGST + D.CGST + D.SGST - IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'Payment','Invoice') - IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'','Invoice'),0),0),0),2) AS RemainingPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
    ELSE
      SET @RemainingPayment = (SELECT 
          ROUND(IFNULL(D.SubTotal-IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'Payment','Invoice'),0),0),2) AS RemainingPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
      SET @RemainingGSTPayment = (SELECT 
          ROUND(IFNULL((D.IGST+0+D.CGST+D.SGST-IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'','Invoice'),0)),0),2) AS RemainingGSTPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
          
        SET @TotalRemainingAmount = (SELECT ROUND(IFNULL(
            D.SubTotal + D.IGST + D.CGST + D.SGST - IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'Payment','Invoice') - IFNULL(Fn_GetPaymentAmount(D.InvoiceID,'','Invoice'),0),0),0),2) AS RemainingPayment FROM ss_invoice D WHERE D.InvoiceID = IFNULL(_InvoiceID,D.InvoiceID));
    END IF;
    UPDATE ss_customerpayment SET RemainingPayment=(@RemainingPayment+@RemainingGSTPayment) WHERE CustomerPaymentID=@LAST_ID;

    IF ((@TotalRemainingAmount = 0.00 OR @TotalRemainingAmount = 0 OR @TotalRemainingAmount = '0')) THEN
    UPDATE  ss_appointment SET
      AppointmentStatus = 'Closed',
      ClosedDateTime = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
      ModifiedBy = IFNULL(_CreatedBy,0),
      ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
      WHERE AppointmentID = _AppointmentID;
      SELECT @LAST_ID AS ID, 'Full' AS PaymentType;
    ELSE
      SELECT @LAST_ID AS ID, 'Partial' AS PaymentType;
    END IF;

COMMIT;
END$$

