CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_A_GetAdvance` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_UserID` VARCHAR(100), IN `_Status` INT)  NO SQL
BEGIN
IF(_UserID = -1) THEN SET _UserID =NULL; END IF; 
IF(_Status = -1 ) THEN SET _Status =NULL; END IF; 

SET @Count = (SELECT 
                COUNT(D.AdvanceID) 
              FROM 
                ss_advance D 
              INNER JOIN ss_user U ON U.UserID=D.UserID
              WHERE 
                D.UserID = IFNULL(_UserID,D.UserID) AND
                D.Status = IFNULL(_Status,D.Status));
IF(_PageSize = -1)THEN
  SET _PageSize = @Count * 10;
  SET _CurrentPage = 1;
END IF;
IF(@Count > 0) THEN 
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(D.AdvanceID,0) AS AdvanceID,
        IFNULL(D.UserID,0) AS UserID,
        IFNULL(Fn_A_ChangeDateFormat(D.AdvanceDate),'') AS AdvanceDate,
        IFNULL(D.Amount,0) AS Amount,
        IFNULL(D.Type,'') AS Type,
        IFNULL(D.Status,0) AS Status,
        IFNULL(U.FirstName,'') AS FirstName,
        IFNULL(U.LastName,'') AS LastName,
        IFNULL(U.MobileNo,'') AS MobileNo,
        IFNULL(E.Salary,'') AS Salary
      FROM ss_advance D
      INNER JOIN ss_user U ON U.UserID=D.UserID
      INNER JOIN ss_employee E ON E.UserID=D.UserID
      WHERE 
       D.UserID = IFNULL(_UserID,D.UserID) AND
       D.Status = IFNULL(_Status,D.Status)
    ) V  ORDER BY V.AdvanceID DESC         
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
