END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_A_GetAppointment` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_AppointmentID` INT, IN `_VisitorID` INT, IN `_ServiceID` INT, IN `_SiteID` INT, IN `_UserID` INT, IN `_AppointmentStatus` ENUM('New','InProgress','Completed','Closed','Rejected','Assigned'), IN `_ActiveStatus` VARCHAR(50), IN `_StartDate` DATE, IN `_EndDate` DATE, IN `_Name` VARCHAR(255), IN `_MobileNo` VARCHAR(20), IN `_AppointmentNo` VARCHAR(20), IN `_FilterType` VARCHAR(20))  NO SQL
