  SET @StartDate = (SELECT DATE_FORMAT(NOW(),"%Y-%m-%d"));
  SET @EndDate = (SELECT DATE_FORMAT(NOW(),"%Y-%m-%d"));
ELSEIF(_FilterType = "Weekly")THEN
  SET @StartDate = (SELECT DATE(NOW() + INTERVAL (0 - WEEKDAY(NOW())) DAY));
  SET @EndDate = (SELECT DATE(NOW() + INTERVAL (6 - WEEKDAY(NOW())) DAY));
ELSEIF(_FilterType = "Monthly")THEN
  SET @StartDate = (SELECT DATE_SUB(CURDATE(), INTERVAL DAY(CURDATE())-1 DAY));
  SET @EndDate = (SELECT LAST_DAY(CURRENT_DATE()));
ELSEIF(_FilterType = "Yearly")THEN
  SET @StartDate = (SELECT MAKEDATE(YEAR(NOW()),1));
  SET @EndDate = (SELECT LAST_DAY(DATE_ADD(NOW(), INTERVAL 12-MONTH(NOW()) MONTH)));
ELSEIF(_FilterType = "Total")THEN
  SET @StartDate = '2010-01-01';
  SET @EndDate = (SELECT CURDATE());
