            IFNULL(Fn_A_ChangeDateTimeFormat(A.ClosedDateTime), '') AS ClosedDateTime,
            CONCAT(TIME_FORMAT(IFNULL(A.TimeSlot, '00:00'), '%H:%i') , '-', TIME_FORMAT(IFNULL(A.EndTime, '23:59'), '%H:%i')) AS TimeSlot,
            IFNULL(Fn_A_ChangeDateFormat(A.StartDate),'') AS StartDate,
