CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_A_GetAvailableProductService` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_InstallationID` INT, IN `_ChallanItemID` INT, IN `_ChallanID` INT, IN `_QuotationID` INT, IN `_VisitorID` INT, IN `_SiteID` INT, IN `_AppointmentID` INT, IN `_SrNo` VARCHAR(20))  NO SQL
BEGIN
