IF(_SiteID = -1 ) THEN SET _SiteID =NULL; END IF;
IF(_AppointmentID = -1 ) THEN SET _AppointmentID =NULL; END IF;

SET @ServiceDueDays = (SELECT ServiceDueDays FROM `ss_config` WHERE ConfigID=1);
    SET @Count = (SELECT 
                    COUNT(I.InstallationID) 
                  FROM ss_installation I
                  INNER JOIN ss_challanitem CI ON CI.ChallanItemID=I.ChallanItemID
                  INNER JOIN ss_challan C ON C.ChallanID=CI.ChallanID
                  INNER JOIN ss_quotation Q ON Q.QuotationID=C.QuotationID
                  INNER JOIN ss_service S ON S.ServiceID=Q.ServiceID
                  LEFT JOIN ss_appointment A ON A.ChallanID=C.ChallanID
                  LEFT JOIN ss_product_service PS ON PS.InstallationID=I.InstallationID
                  WHERE 
                  I.InstallationID = IFNULL(_InstallationID,I.InstallationID) AND
                  I.ChallanItemID = IFNULL(_ChallanItemID,I.ChallanItemID) AND
                  CI.ChallanID = IFNULL(_ChallanID,CI.ChallanID) AND
                  Q.QuotationID = IFNULL(_QuotationID,Q.QuotationID) AND
                  Q.VisitorID = IFNULL(_VisitorID,Q.VisitorID) AND
                  Q.SitesID = IFNULL(_SiteID,Q.SitesID) AND
                  A.AppointmentID = IFNULL(_AppointmentID,A.AppointmentID) AND
                  CI.SrNo LIKE CONCAT('%',IFNULL(_SrNo,CI.SrNo),'%')  AND
                  CI.HasChallan = 1 AND 
                  CURRENT_DATE() > DATE_ADD(PS.ServiceDate, INTERVAL (@ServiceDueDays*-1) DAY) AND
                  CURRENT_DATE() < DATE_ADD(PS.ServiceDate, INTERVAL @ServiceDueDays DAY));
    IF(_PageSize = -1)THEN
        SET _PageSize = @Count * 10;
        SET _CurrentPage = 1;
    END IF;
    IF(@Count > 0) THEN 
    SET @rownum:=0;
    SELECT AA.*,@rownum AS rowcount FROM (
        SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(I.InstallationID,0) AS InstallationID,
        IFNULL(I.ChallanItemID,0) AS ChallanItemID,
        IFNULL(I.IndoorSrNo,0) AS IndoorSrNo,
        IFNULL(I.OutdoorSrNo,0) AS OutdoorSrNo,
        IFNULL(I.InstallationType,0) AS InstallationType,
        IFNULL(I.CopperPiping,0) AS CopperPiping,
        IFNULL(I.Cable,0) AS Cable,
        IFNULL(I.Drain,0) AS Drain,
        IFNULL(I.ZariWork,0) AS ZariWork,
        IFNULL(I.CoreCutting,0) AS CoreCutting,
        IFNULL(I.StandType,0) AS StandType,
        IFNULL(I.UnitSetup,0) AS UnitSetup,
        IFNULL(I.Piping,0) AS Piping,
        
        IFNULL(I.CopperPipingPayable,0) AS CopperPipingPayable,
        IFNULL(I.CablePayable,0) AS CablePayable,
        IFNULL(I.DrainPayable,0) AS DrainPayable,
        IFNULL(I.ZariWorkPayable,0) AS ZariWorkPayable,
        IFNULL(I.CoreCuttingPayable,0) AS CoreCuttingPayable,
        IFNULL(I.StandTypePayable,0) AS StandTypePayable,
        IFNULL(I.CopperPipingRemark,'') AS CopperPipingRemark,
        IFNULL(I.CableRemark,'') AS CableRemark,
        IFNULL(I.DrainRemark,'') AS DrainRemark,
        IFNULL(I.ZariWorkRemark,'') AS ZariWorkRemark,
        IFNULL(I.CoreCuttingRemark,'') AS CoreCuttingRemark,
        IFNULL(I.StandTypeRemark,'') AS StandTypeRemark,

        IFNULL(I.Remark,0) AS Remark,
        IFNULL(I.Status,0) AS Status,
        IFNULL(I.Service,0) AS NumberOfService,
        IFNULL(I.Warranty,0) AS WarrantyDays,

        IFNULL(Fn_A_ChangeDateFormat(I.CreatedDate),'') AS InstallationDate,
        IFNULL(P.ProductName,'') AS ProductName,
        IFNULL(P.DisplayName,'') AS DisplayName,
        IFNULL(P.Model,'') AS Model,
        IFNULL(P.Capacity,'') AS Capacity,
        IFNULL(P.Specification,'') AS Specification,
        IFNULL(P.HSNNo,'') AS PHSNNo,
        IFNULL(P.BrandID,0) AS BrandID,
        IFNULL(B.BrandName,0) AS BrandName,
        IFNULL(S.Service,0) AS Service,
        IFNULL(CI.ChallanID,0) AS ChallanID,
        IFNULL(CI.SrNo,0) AS SrNo,
        IFNULL(I.Location,0) AS Location,
        IFNULL(Q.SitesID,0) AS SitesID,
        IFNULL(PS.ServiceID,0) AS ServiceID

    FROM ss_installation I
    INNER JOIN ss_challanitem CI ON CI.ChallanItemID=I.ChallanItemID
    INNER JOIN ss_challan C ON C.ChallanID=CI.ChallanID
    INNER JOIN ss_quotation Q ON Q.QuotationID=C.QuotationID
    INNER JOIN ss_service S ON S.ServiceID=Q.ServiceID
    LEFT JOIN ss_product P ON P.ProductID=CI.ProductID
    LEFT JOIN ss_brand B ON B.BrandID=P.BrandID
    LEFT JOIN ss_appointment A ON A.ChallanID=C.ChallanID
    LEFT JOIN ss_product_service PS ON PS.InstallationID=I.InstallationID
    WHERE 
    I.InstallationID = IFNULL(_InstallationID,I.InstallationID) AND
    I.ChallanItemID = IFNULL(_ChallanItemID,I.ChallanItemID) AND
    CI.ChallanID = IFNULL(_ChallanID,CI.ChallanID) AND
    Q.QuotationID = IFNULL(_QuotationID,Q.QuotationID) AND
    Q.VisitorID = IFNULL(_VisitorID,Q.VisitorID) AND
    Q.SitesID = IFNULL(_SiteID,Q.SitesID) AND
    A.AppointmentID = IFNULL(_AppointmentID,A.AppointmentID) AND
    CI.SrNo LIKE CONCAT('%',IFNULL(_SrNo,CI.SrNo),'%') AND
    CI.HasChallan = 1 AND 
    CURRENT_DATE() > DATE_ADD(PS.ServiceDate, INTERVAL (@ServiceDueDays*-1) DAY) AND
    CURRENT_DATE() < DATE_ADD(PS.ServiceDate, INTERVAL @ServiceDueDays DAY)
    ORDER BY I.InstallationID DESC
    ) V  ORDER BY V.ChallanItemID         
    ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
    ELSE 
        SELECT Fn_M_getErrorMessage('Challan_not_found') AS Message;
    END IF;
END$$

