BEGIN
IF(_ServiceID = -1 ) THEN SET _ServiceID =NULL; END IF;
IF(_VisitorID = -1 ) THEN SET _VisitorID =NULL; END IF;
IF(_ServiceStatus = '' ) THEN SET _ServiceStatus =NULL; END IF;

SET @Count = (SELECT 
                COUNT(S.ServiceID) 
                FROM ss_product_service S
                INNER JOIN ss_challanitem CI ON CI.ChallanItemID=S.ChallanItemID
                INNER JOIN ss_challan C ON C.ChallanID=CI.ChallanID
                INNER JOIN ss_quotation Q ON Q.QuotationID=C.QuotationID
                INNER JOIN ss_installation I ON I.InstallationID=S.InstallationID
                WHERE 
                S.ChallanItemID in (SELECT ChallanItemID FROM ss_product_service WHERE ServiceID = IFNULL(_ServiceID,ServiceID)) AND
                S.ServiceStatus = IFNULL(_ServiceStatus,S.ServiceStatus) AND
                Q.VisitorID = IFNULL(_VisitorID,Q.VisitorID)
              );
IF(_PageSize = -1 ) THEN 
  SET _PageSize = @Count*10; 
  SET _CurrentPage = 1; 
END IF; 
SET @ServiceDueDays = (SELECT ServiceDueDays FROM `ss_config` WHERE ConfigID=1);
IF(@Count > 0) THEN
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
        SELECT
        IFNULL(S.ServiceID,0) AS ServiceID,
        IFNULL(Fn_A_ChangeDateFormat(S.ServiceDate),'') AS ServiceDate,
        IFNULL(S.ServiceStatus,'') AS ServiceStatus,
        IFNULL(S.ServiceType,'') AS ServiceType,
        IFNULL(M.Material,'') AS Material,
        IFNULL(M.HSNNo,'') AS HSNNo,
        IFNULL(P.ProductName,'') AS ProductName,
        IFNULL(P.DisplayName,'') AS DisplayName,
        IFNULL(P.Model,'') AS Model,
        IFNULL(P.Capacity,'') AS Capacity,
        IFNULL(P.Specification,'') AS Specification,
        IFNULL(P.HSNNo,'') AS PHSNNo,
        IFNULL(B.BrandName,'') AS BrandName,
        IFNULL(I.IndoorSrNo,'') AS IndoorSrNo,
        IFNULL(I.OutdoorSrNo,'') AS OutdoorSrNo,
        IFNULL(CI.SrNo,'') AS SrNo,
        IFNULL(Fn_A_ChangeDateFormat(I.CreatedDate),'') AS InstallationDate,
        IFNULL(I.Location,'') AS Location,
        IFNULL(Q.CustomerID,'') AS CustomerID,
        IFNULL(ST.SitesID,'') AS SitesID,
        IFNULL(ST.SiteName,'') AS SiteName,
        IFNULL(V.Name,'') AS VisitorName,
        IFNULL(V.MobileNo,'') AS MobileNo,
        IFNULL(V.VisitorID,'') AS VisitorID,
        (SELECT IF(CURRENT_DATE() > DATE_ADD(S.ServiceDate, INTERVAL (@ServiceDueDays*-1) DAY) AND  CURRENT_DATE() < DATE_ADD(S.ServiceDate, INTERVAL @ServiceDueDays DAY), 1, 0)) AS IfServiceTime
        FROM ss_product_service S
        INNER JOIN ss_challanitem CI ON CI.ChallanItemID=S.ChallanItemID
        INNER JOIN ss_challan C ON C.ChallanID=CI.ChallanID
        INNER JOIN ss_quotation Q ON Q.QuotationID=C.QuotationID
        INNER JOIN ss_installation I ON I.InstallationID=S.InstallationID
        LEFT JOIN ss_sites ST ON ST.SitesID=Q.SitesID
        LEFT JOIN ss_visitor V ON V.VisitorID=Q.VisitorID
        LEFT JOIN ss_product P ON P.ProductID=CI.ProductID
        LEFT JOIN ss_brand B ON B.BrandID=P.BrandID
        LEFT JOIN ss_material M ON M.MaterialID=CI.MaterialID
        WHERE 
        S.ChallanItemID in (SELECT ChallanItemID FROM ss_product_service WHERE ServiceID = IFNULL(_ServiceID,ServiceID)) AND
        S.ServiceStatus = IFNULL(_ServiceStatus,S.ServiceStatus) AND
        Q.VisitorID = IFNULL(_VisitorID,Q.VisitorID)
        ORDER BY S.ServiceID ASC
    ) V       
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
ELSE 
    SELECT Fn_M_getErrorMessage('product_not_found') AS Message;
END IF;
END$$

