CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_A_GetServiceWiseCollection` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_FilterType` VARCHAR(50))  NO SQL
BEGIN

IF(_FilterType = "Daily")THEN
  SET @StartDate = (SELECT DATE_FORMAT(NOW(),"%Y-%m-%d"));
  SET @EndDate = (SELECT DATE_FORMAT(NOW(),"%Y-%m-%d"));
ELSEIF(_FilterType = "Weekly")THEN
  SET @StartDate = (SELECT DATE(NOW() + INTERVAL (0 - WEEKDAY(NOW())) DAY));
  SET @EndDate = (SELECT DATE(NOW() + INTERVAL (6 - WEEKDAY(NOW())) DAY));
ELSEIF(_FilterType = "Monthly")THEN
  SET @StartDate = (SELECT DATE_SUB(CURDATE(), INTERVAL DAY(CURDATE())-1 DAY));
  SET @EndDate = (SELECT LAST_DAY(CURRENT_DATE()));
ELSEIF(_FilterType = "Yearly")THEN
  SET @StartDate = (SELECT MAKEDATE(YEAR(NOW()),1));
  SET @EndDate = (SELECT LAST_DAY(DATE_ADD(NOW(), INTERVAL 12-MONTH(NOW()) MONTH)));
ELSEIF(_FilterType = "Total")THEN
  SET @StartDate = '2010-10-10';
  SET @EndDate = (SELECT CURDATE());
END IF;

SET @Count = (SELECT 
                COUNT(D.QuotationID) 
              FROM ss_quotation D
              INNER JOIN ss_service S ON S.ServiceID=D.ServiceID
              WHERE 
                @StartDate <= DATE_FORMAT(D.StartDate,"%Y-%m-%d") AND 
                @EndDate >= DATE_FORMAT(D.StartDate,"%Y-%m-%d") AND
                D.QuotationStatus = 'Accept' AND
                D.CustomerID > 0
              );
IF(_PageSize = -1)THEN
  SET _PageSize = @Count * 10;
  SET _CurrentPage = 1;
END IF;
IF(@Count > 0) THEN 
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(SUM(D.TotalAmount),'') AS TotalAmount,
        IFNULL(SUM(D.GSTAmount),'') AS GSTAmount,
        IFNULL(S.Service,'') AS ServiceName
      FROM ss_quotation D
      INNER JOIN ss_service S ON S.ServiceID=D.ServiceID
      WHERE 
            @StartDate <= DATE_FORMAT(D.StartDate,"%Y-%m-%d") AND 
            @EndDate >= DATE_FORMAT(D.StartDate,"%Y-%m-%d") AND
            D.QuotationStatus = 'Accept' AND
            D.CustomerID > 0
      GROUP BY D.ServiceID
    ) V  ORDER BY V.TotalAmount DESC         
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
ELSE 
    SELECT Fn_M_getErrorMessage('quotation_listing_not_found') AS Message;
END IF;
END$$

