BEGIN
 GET DIAGNOSTICS CONDITION 1 @sqlstate = RETURNED_SQLSTATE, @errno = MYSQL_ERRNO, @textMsg = MESSAGE_TEXT; 
 SELECT Fn_M_getErrorMessage('something_went_wrong_on_server_please_contact_to_admin') AS Message;
