      Reason = IFNULL(_Reason,''),
      AppointmentStatus = IFNULL(_AppointmentStatus,''),
      StartDate = IFNULL(_StartDate,'1990-01-01'),
      TimeSlot = IFNULL(_StartTime,'00:00'),
      EndDate = IFNULL(_EndDate,'1990-01-01'),
      EndTime = IFNULL(_EndTime,'23:59'),
      Remark = IFNULL(_Remark,''),
      ModifiedBy = IFNULL(_ModifiedBy,0),
      Status = IFNULL(_Status, 0),
      ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
      WHERE AppointmentID = _AppointmentID;
 SET @_ID=(SELECT Fn_A_AddActivityLog('EditTeamDefination',' has updated TeamDefination',_UserType,_AppointmentID,_IPAddress,_ModifiedBy));
 SELECT _AppointmentID AS ID;
ELSE 
 SELECT Fn_M_getErrorMessage('TeamDefination_not_found') AS Message;
END IF;
COMMIT;
END$$

