CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_M_GetEmployee` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_Name` VARCHAR(100), IN `_EmailID` VARCHAR(250), IN `_CityID` INT)  NO SQL
BEGIN
IF(_Name = '') THEN SET _Name =NULL; END IF; 
IF(_EmailID = '') THEN SET _EmailID =NULL; END IF; 
IF(_CityID = -1) THEN SET _CityID =NULL; END IF; 

SET @Count = (SELECT 
                COUNT(U.UserID) 
              FROM 
                ss_user U 
              INNER JOIN ss_employee E ON E.UserID=U.UserID
              INNER JOIN ss_usertype T ON T.UsertypeID = E.UsertypeID
              WHERE 
                (
                  U.FirstName LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%') OR
                  U.LastName LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%') OR
                  CONCAT(U.FirstName,' ',U.LastName) LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%')
                ) AND
                (
                  U.EmailID LIKE CONCAT("%",IFNULL(_EmailID,U.EmailID),"%") OR
                  U.MobileNo LIKE CONCAT("%",IFNULL(_EmailID,U.MobileNo),"%")
                ) AND
                U.UserType <> "Employee" AND U.UserType <> "Admin" AND
                E.CityID = IFNULL(_CityID,E.CityID)
              );
IF(_PageSize = -1)THEN
  SET _PageSize = @Count * 10;
  SET _CurrentPage = 1;
END IF;
IF(@Count > 0) THEN 
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(U.UserID,0) AS UserID, 
        IFNULL(U.RoleID,0) AS RoleID, 
        IFNULL(U.EmailID,'') AS EmailID, 
        IFNULL(U.FirstName,'') AS FirstName, 
        IFNULL(U.LastName,'') AS LastName, 
        IFNULL(U.MobileNo,'') AS MobileNo, 
        IFNULL(U.Address,'') AS Address, 
        IFNULL(U.IsDeleted,0) AS IsDeleted, 
        IFNULL(U.Status,0) AS Status,
        IFNULL(E.Salary,0) AS Salary,
        IFNULL(E.UsertypeID,0) AS UsertypeID,
        IFNULL(T.Usertype,'') AS Usertype,
        IFNULL(E.WorkingHours,'') AS WorkingHours,
        IFNULL(E.Documents,'') AS Documents,
        IFNULL(E.OfferLetter,'') AS OfferLetter,
        IFNULL(E.BankName,'') AS BankName,
        IFNULL(E.BranchName,'') AS BranchName,
        IFNULL(E.AccountNo,'') AS AccountNo,
        IFNULL(E.IFSCCode,'') AS IFSCCode
      FROM ss_user U
      INNER JOIN ss_employee E ON E.UserID=U.UserID
      INNER JOIN ss_usertype T ON T.UsertypeID = E.UsertypeID
      WHERE 
        (
          U.FirstName LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%') OR
          U.LastName LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%') OR
          CONCAT(U.FirstName,' ',U.LastName) LIKE CONCAT('%',IFNULL(_Name,U.FirstName),'%')
        ) AND
        (
          U.EmailID LIKE CONCAT("%",IFNULL(_EmailID,U.EmailID),"%") OR
          U.MobileNo LIKE CONCAT("%",IFNULL(_EmailID,U.MobileNo),"%")
        ) AND
        U.UserType <> "Employee" AND U.UserType <> "Admin" AND
        E.CityID = IFNULL(_CityID,E.CityID)
      ORDER BY U.FirstName,U.LastName
    ) V  ORDER BY V.FirstName,V.LastName
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
ELSE 
    SELECT Fn_M_getErrorMessage('user_listing_not_found') AS Message;
END IF;
END$$

