CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_M_GetTrainingDateTime` (IN `_PageSize` INT, IN `_CurrentPage` INT, IN `_Status` INT)  NO SQL
BEGIN
IF(_Status = -1 ) THEN SET _Status =NULL; END IF; 

SET @Count = (SELECT 
                COUNT(D.TrainingDateTimeID) 
              FROM 
                ss_trainingdatetime D 
              INNER JOIN ss_training T ON T.TrainingID=D.TrainingID
              WHERE 
                D.Status = IFNULL(_Status,D.Status));
IF(_PageSize = -1)THEN
  SET _PageSize = @Count * 10;
  SET _CurrentPage = 1;
END IF;
IF(@Count > 0) THEN 
  SET @rownum:=0;
  SELECT AA.*,@rownum AS rowcount FROM (
    SELECT V.*, @rownum := @rownum + 1 AS Rno FROM ( 
      SELECT 
        IFNULL(D.TrainingDateTimeID,0) AS TrainingDateTimeID,
        IFNULL(D.TrainingID,0) AS TrainingID,
        IFNULL(T.Training,'') AS Training,
        Fn_A_ChangeDateFormat(D.TrainingDate) AS TrainingDate,
        IFNULL(D.TrainingTime,'') AS TrainingTime,
        IFNULL(D.Status,0) AS Status
      FROM ss_trainingdatetime D
      INNER JOIN ss_training T ON T.TrainingID=D.TrainingID
      WHERE 
       D.Status = IFNULL(_Status,D.Status)
    ) V  ORDER BY V.TrainingDateTimeID DESC  
  ) AA WHERE Rno BETWEEN (((_CurrentPage*_PageSize) - _PageSize)+1) AND (_CurrentPage*_PageSize);
ELSE 
    SELECT Fn_M_getErrorMessage('trainingdatetime_listing_not_found') AS Message;
END IF;
END$$

