CREATE DEFINER=`root`@`localhost` PROCEDURE `usp_ResetPassword` (IN `_EmailID` VARCHAR(250), IN `_Password` VARCHAR(100))  NO SQL
BEGIN
SET @CNT = (SELECT COUNT(U.EmailID) FROM ss_user U WHERE (U.EmailID = _EmailID OR U.MobileNo = _EmailID) AND Status = 1);
IF(@CNT > 0) THEN 
	SET @Resultdelete = (SELECT COUNT(U.EmailID) FROM ss_user U WHERE (U.EmailID = _EmailID OR U.MobileNo = _EmailID) AND U.IsDeleted = 0);
	IF(@Resultdelete > 0) THEN
		UPDATE ss_user
		SET Password = _Password
		WHERE 
			EmailID = _EmailID;
		SELECT 1 AS ID;
  	ELSE 
    	SELECT Fn_M_getErrorMessage('user_deleted_contact_to_admin') AS Message;
  	END IF;
ELSE
	SELECT Fn_M_getErrorMessage('user_not_found') AS Message;
END IF;
END$$

