CREATE DEFINER=`root`@`localhost` FUNCTION `Fn_M_AddDevice` (`_DeviceName` VARCHAR(100), `_DeviceUID` VARCHAR(50), `_OSVersion` VARCHAR(10), `_DeviceTokenID` TEXT, `_DeviceType` VARCHAR(50), `_UserID` INT(11), `_DeviceOS` TEXT, `_UserType` VARCHAR(50)) RETURNS INT(11) NO SQL
BEGIN 

  DECLARE User_Type VARCHAR(50) DEFAULT 0;
  
SET @cnt = (SELECT COUNT(DeviceID) FROM ss_deviceinfo WHERE DeviceUID = _DeviceUID);
SET @TimeZone = (SELECT Fn_TimeZone());
  
IF(@cnt > 0) THEN 
   SET @ID = (SELECT DeviceID FROM ss_deviceinfo WHERE DeviceUID = _DeviceUID);

   UPDATE ss_deviceinfo 
   SET 
    DeviceName = IFNULL(_DeviceName,''),
    DeviceOS = IFNULL(_DeviceOS,''),
    DeviceUID = IFNULL(_DeviceUID,''),
    DeviceTokenID = IFNULL(_DeviceTokenID,''),
    OSVersion = IFNULL(_OSVersion,''),
    DeviceType = IFNULL(_DeviceType,''),
    UserType = IFNULL(_UserType,''),
    UserID = IFNULL(_UserID,0),
    ModifiedBy = IFNULL(_UserID,0),
    ModifiedDate = CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
  WHERE 
    DeviceUID = _DeviceUID;
ELSE 

INSERT INTO ss_deviceinfo  
(DeviceName,DeviceOS,DeviceUID,OSVersion,DeviceTokenID,DeviceType,UserType,UserID,CreatedBy,CreatedDate,ModifiedBy,ModifiedDate)
VALUES(
    IFNULL(_DeviceName,''),
    IFNULL(_DeviceOS,''),
    IFNULL(_DeviceUID,''),
    IFNULL(_OSVersion,''),
    IFNULL(_DeviceTokenID,''),
    IFNULL(_DeviceType,''),
    IFNULL(_UserType,''),
    IFNULL(_UserID,0),
    IFNULL(_UserID,0),
    CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone),
    IFNULL(_UserID,0),
    CONVERT_TZ(NOW(),@@session.time_zone,@TimeZone)
);
  SET @LAST_ID = LAST_INSERT_ID();
END IF;
RETURN 1;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `Fn_M_getErrorMessage` (`_MessageKey` VARCHAR(150)) RETURNS TEXT CHARSET utf8 COLLATE utf8_general_ci NO SQL
BEGIN 

DECLARE ErrorMessage varchar(250) DEFAULT 0;

  SELECT Concat(ErrorCode,'~',Message)
  INTO ErrorMessage
  FROM ss_messages
  WHERE MessageKey = _MessageKey AND MessageLanguage='english' LIMIT 1;

IF(IFNULL(ErrorMessage,'')='') THEN SET ErrorMessage = concat(103,'~',_MessageKey); END IF;
RETURN ErrorMessage;

END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `Fn_TimeZone` () RETURNS TEXT CHARSET latin1 COLLATE latin1_swedish_ci NO SQL
BEGIN
SET @TimeZone = (SELECT TimeZone  FROM ss_config Limit 1);

IF(@TimeZone = "") THEN 

  SET @TimeZone = "+00:00"; END IF;
 
RETURN @TimeZone;
END$$

